import React from 'react';

const UpgradeScreen = ({ exp, onUpgrade, onContinue }) => {
  const [damageLevel, setDamageLevel] = React.useState(0);
  const [hpLevel, setHpLevel] = React.useState(0);

  const calculateCost = (level) => 1 + level + level;
  const calculateIncrease = (level) => 1 + level;

  const damageCost = calculateCost(damageLevel);
  const hpCost = calculateCost(hpLevel);

  const handleUpgradeDamage = () => {
    if (exp >= damageCost) {
      setDamageLevel(prevLevel => prevLevel + 1);
      onUpgrade('damage', calculateIncrease(damageLevel), damageCost);
    }
  };

  const handleUpgradeHP = () => {
    if (exp >= hpCost) {
      setHpLevel(prevLevel => prevLevel + 1);
      onUpgrade('hp', calculateIncrease(hpLevel), hpCost);
    }
  };

  return (
    <div className="upgrade-screen" style={{ padding: '20px', backgroundColor: '#f0f0f0', borderRadius: '10px' }}>
      <h2>Upgrade Your Monster</h2>
      <p>Available EXP: {exp}</p>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <div>
          <h3>Damage</h3>
          <p>Current Level: {damageLevel}</p>
          <p>Cost: {damageCost} EXP</p>
          <p>Increase: +{calculateIncrease(damageLevel)}</p>
          <button onClick={handleUpgradeDamage} disabled={exp < damageCost}>Upgrade Damage</button>
        </div>
        <div>
          <h3>HP</h3>
          <p>Current Level: {hpLevel}</p>
          <p>Cost: {hpCost} EXP</p>
          <p>Increase: +{calculateIncrease(hpLevel)}</p>
          <button onClick={handleUpgradeHP} disabled={exp < hpCost}>Upgrade HP</button>
        </div>
      </div>
      <button onClick={onContinue} style={{ marginTop: '20px' }}>Continue to Next Wave</button>
    </div>
  );
};

export default UpgradeScreen;
