import React from 'react';

const Timer = ({ cooldown, maxCooldown }) => {
  const percentage = (cooldown / maxCooldown) * 100;
  
  return (
    <div style={{ width: '100px', height: '10px', border: '1px solid black', position: 'relative' }}>
      <div 
        style={{
          width: `${percentage}%`,
          height: '100%',
          backgroundColor: 'blue',
          transition: 'width 0.1s linear'
        }}
      />
      <div style={{ position: 'absolute', top: 0, width: '100%', textAlign: 'center', fontSize: '10px' }}>
        {(cooldown / 1000).toFixed(1)}s
      </div>
    </div>
  );
};

export default Timer;
