import React from 'react';
import { useGameContext } from './GameContext';
import Monster from './Monster';
import Hero from './Hero';
import HealthBar from './HealthBar';

const BattleArea = () => {
  const { 
    wave, exp, monsterHP, monsterMaxHP, monsterDamage,
    heroHP, heroMaxHP, heroDamage
  } = useGameContext();

  return (
    <div className="battle-area">
      <div className="info-bar">
        Wave: {wave} EXP: {exp}
      </div>
      <div className="characters">
        <div className="character monster">
          <Monster />
          <HealthBar current={monsterHP} max={monsterMaxHP} />
          <div className="damage">Damage: {monsterDamage}</div>
        </div>
        <div className="character hero">
          <Hero />
          <HealthBar current={heroHP} max={heroMaxHP} />
          <div className="damage">Damage: {heroDamage}</div>
        </div>
      </div>
    </div>
  );
};

export default BattleArea;
