import React from 'react';

const GameOverScreen = ({ wave, exp, onRestart }) => {
  return (
    <div className="game-over-screen" style={{ textAlign: 'center', padding: '20px' }}>
      <h1>Game Over!</h1>
      <p>You survived {wave} waves</p>
      <p>Total EXP earned: {exp}</p>
      <button onClick={onRestart} style={{ fontSize: '1.2em', padding: '10px 20px', marginTop: '20px' }}>
        Play Again
      </button>
    </div>
  );
};

export default GameOverScreen;
