import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';

const GameContext = createContext();

export const useGameContext = () => useContext(GameContext);

export const GameProvider = ({ children }) => {
  const [gameState, setGameState] = useState('start');
  const [wave, setWave] = useState(1);
  const [exp, setExp] = useState(0);
  const [monsterHP, setMonsterHP] = useState(10);
  const [monsterMaxHP, setMonsterMaxHP] = useState(10);
  const [monsterDamage, setMonsterDamage] = useState(1);
  const [heroHP, setHeroHP] = useState(10);
  const [heroMaxHP, setHeroMaxHP] = useState(10);
  const [heroDamage, setHeroDamage] = useState(1);
  const [monsterCooldown, setMonsterCooldown] = useState(5000);
  const [heroCooldown, setHeroCooldown] = useState(3000);
  const [isTargetVisible, setIsTargetVisible] = useState(false);

  const startGame = useCallback(() => {
    setGameState('playing');
    setWave(1);
    setMonsterHP(10);
    setMonsterMaxHP(10);
    setMonsterDamage(1);
    setHeroHP(10);
    setHeroMaxHP(10);
    setHeroDamage(1);
    setExp(0);
    setMonsterCooldown(5000);
    setHeroCooldown(3000);
  }, []);

  const handleTargetClick = useCallback(() => {
    setMonsterCooldown(prev => Math.max(0, prev - 1000));
    setIsTargetVisible(false);
  }, []);

  useEffect(() => {
    if (gameState !== 'playing') return;

    const gameLoop = setInterval(() => {
      setMonsterCooldown(prev => Math.max(0, prev - 100));
      setHeroCooldown(prev => Math.max(0, prev - 100));

      if (monsterCooldown === 0) {
        setHeroHP(prev => Math.max(0, prev - monsterDamage));
        setMonsterCooldown(5000);
      }

      if (heroCooldown === 0) {
        setMonsterHP(prev => Math.max(0, prev - heroDamage));
        setHeroCooldown(3000);
      }

      if (Math.random() < 0.1 && !isTargetVisible) {
        setIsTargetVisible(true);
      }

      if (monsterHP <= 0 || heroHP <= 0) {
        setGameState('gameOver');
      }
    }, 100);

    return () => clearInterval(gameLoop);
  }, [gameState, monsterCooldown, heroCooldown, monsterDamage, heroDamage, monsterHP, heroHP, isTargetVisible]);

  const value = {
    gameState,
    setGameState,
    wave,
    setWave,
    exp,
    setExp,
    monsterHP,
    setMonsterHP,
    monsterMaxHP,
    setMonsterMaxHP,
    monsterDamage,
    setMonsterDamage,
    heroHP,
    setHeroHP,
    heroMaxHP,
    setHeroMaxHP,
    heroDamage,
    setHeroDamage,
    monsterCooldown,
    heroCooldown,
    isTargetVisible,
    startGame,
    handleTargetClick,
  };

  return <GameContext.Provider value={value}>{children}</GameContext.Provider>;
};

export default GameContext;
