import React from 'react';

const StartScreen = ({ onStart }) => {
  return (
    <div className="start-screen" style={{ textAlign: 'center', padding: '20px' }}>
      <h1>Monstrous Ascendancy: Quick Strike</h1>
      <p>Survive waves of heroes, upgrade your monster, and see how long you can last!</p>
      <button onClick={onStart} style={{ fontSize: '1.2em', padding: '10px 20px', marginTop: '20px' }}>
        Start Game
      </button>
    </div>
  );
};

export default StartScreen;
