import React, { useState, useEffect, useCallback } from 'react';
import Monster from './Monster';
import Hero from './Hero';
import HealthBar from './HealthBar';
import TargetButton from './TargetButton';
import GameOverScreen from './GameOverScreen';
import Timer from './Timer';
import './App.css';

const Game = () => {
  const [gameState, setGameState] = useState('start');
  const [wave, setWave] = useState(1);
  const [monsterHP, setMonsterHP] = useState(1);
  const [monsterMaxHP, setMonsterMaxHP] = useState(1);
  const [monsterDamage, setMonsterDamage] = useState(1);
  const [heroHP, setHeroHP] = useState(1);
  const [heroMaxHP, setHeroMaxHP] = useState(1);
  const [heroDamage, setHeroDamage] = useState(1);
  const [exp, setExp] = useState(0);
  const [isTargetVisible, setIsTargetVisible] = useState(false);
  const [monsterCooldown, setMonsterCooldown] = useState(5000);
  const [heroCooldown, setHeroCooldown] = useState(3000);
  const [damageLevel, setDamageLevel] = useState(1);
  const [healthLevel, setHealthLevel] = useState(1);

  const damageScale = [1, 2, 3, 5, 7, 9, 12, 15, 18, 21];
  const healthScale = [1, 2, 4, 6, 8, 12, 16, 20, 26, 32];

  const showTarget = useCallback(() => {
    setIsTargetVisible(true);
  }, []);

  useEffect(() => {
    if (gameState === 'playing') {
      const monsterAttackInterval = setInterval(() => {
        setHeroHP((prevHP) => Math.max(0, prevHP - monsterDamage));
        setMonsterCooldown(5000);
      }, monsterCooldown);

      const heroAttackInterval = setInterval(() => {
        setMonsterHP((prevHP) => Math.max(0, prevHP - heroDamage));
        setHeroCooldown(3000);
      }, heroCooldown);

      const cooldownTickInterval = setInterval(() => {
        setMonsterCooldown((prev) => Math.max(0, prev - 100));
        setHeroCooldown((prev) => Math.max(0, prev - 100));
      }, 100);

      // Show target after 1 second
      const targetTimeout = setTimeout(showTarget, 1000);

      return () => {
        clearInterval(monsterAttackInterval);
        clearInterval(heroAttackInterval);
        clearInterval(cooldownTickInterval);
        clearTimeout(targetTimeout);
      };
    }
  }, [gameState, monsterDamage, heroDamage, monsterCooldown, heroCooldown, showTarget]);

  useEffect(() => {
    if (heroHP <= 0 && gameState === 'playing') {
      setExp((prevExp) => prevExp + wave);
      setWave((prevWave) => prevWave + 1);
      startNewWave();
    } else if (monsterHP <= 0 && gameState === 'playing') {
      setGameState('gameOver');
    }
  }, [heroHP, monsterHP, wave, gameState]);

  const handleUpgrade = (type) => {
    const cost = type === 'damage' ? damageLevel : healthLevel;
    if (exp >= cost) {
      if (type === 'damage') {
        setDamageLevel((prev) => prev + 1);
        setMonsterDamage(damageScale[Math.min(damageLevel, damageScale.length - 1)]);
      } else if (type === 'health') {
        setHealthLevel((prev) => prev + 1);
        const newHealth = healthScale[Math.min(healthLevel, healthScale.length - 1)];
        setMonsterMaxHP(newHealth);
        setMonsterHP(newHealth);
      }
      setExp((prev) => prev - cost);
    }
  };

  const startNewWave = () => {
    setHeroHP(wave);
    setHeroMaxHP(wave);
    setHeroDamage(wave);
    setMonsterHP(monsterMaxHP);
    setIsTargetVisible(false);
  };

  const handleTargetClick = () => {
    setMonsterCooldown(prev => Math.max(0, prev - 250));
    setIsTargetVisible(false);
    setTimeout(showTarget, 1000);
  };

  return (
    <div className="game-container">
      {gameState === 'start' && (
        <button onClick={() => setGameState('playing')}>Start Game</button>
      )}
      {gameState === 'playing' && (
        <>
          <div className="info-bar">
            <span>Wave: {wave}</span>
            <span>EXP: {exp}</span>
          </div>
          <div className="battle-area">
            <div className="monster-container">
              <Monster />
              <HealthBar current={monsterHP} max={monsterMaxHP} />
              <Timer cooldown={monsterCooldown} maxCooldown={5000} />
              <div>Damage: {monsterDamage} (Level {damageLevel})</div>
              <div>Health: {monsterMaxHP} (Level {healthLevel})</div>
            </div>
            <div className="hero-container">
              <Hero />
              <HealthBar current={heroHP} max={heroMaxHP} />
              <Timer cooldown={heroCooldown} maxCooldown={3000} />
              <TargetButton 
                onClick={handleTargetClick} 
                isVisible={isTargetVisible} 
              />
            </div>
          </div>
        </>
      )}
      {gameState === 'gameOver' && (
        <GameOverScreen 
          wave={wave} 
          exp={exp} 
          onUpgrade={handleUpgrade}
          damageCost={damageLevel}
          healthCost={healthLevel}
          onRestart={() => {
            setGameState('start');
            setWave(1);
            setMonsterHP(1);
            setMonsterMaxHP(1);
            setMonsterDamage(1);
            setHeroHP(1);
            setHeroMaxHP(1);
            setHeroDamage(1);
            setExp(0);
            setDamageLevel(1);
            setHealthLevel(1);
          }} 
        />
      )}
    </div>
  );
};

export default Game;
