// src/GameOverScreen.js
import React from 'react';

const GameOverScreen = ({ wave, exp, onUpgrade, onRestart }) => {
  return (
    <div className="game-over-screen">
      <h2>Game Over</h2>
      <p>You survived {wave - 1} waves</p>
      <p>Total EXP: {exp}</p>
      <div className="upgrade-options">
        <button onClick={() => onUpgrade('damage', 1)}>
          Upgrade Damage (10 EXP)
        </button>
        <button onClick={() => onUpgrade('health', 10)}>
          Upgrade Health (10 EXP)
        </button>
      </div>
      <button onClick={onRestart}>Restart Game</button>
    </div>
  );
};

export default GameOverScreen;
