import React from 'react';

const TargetButton = ({ onClick, isVisible }) => {
  if (!isVisible) return null;

  return (
    <button 
      onClick={onClick}
      style={{
        width: '50px',
        height: '50px',
        backgroundColor: 'yellow',
        border: '4px solid black',
        cursor: 'pointer',
        position: 'absolute',
        top: '50%',
        left: '75%',
        transform: 'translate(-50%, -50%)',
        zIndex: 10,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        fontSize: '24px',
        fontWeight: 'bold',
        color: 'black'
      }}
    >
      X
    </button>
  );
};

export default TargetButton;
