/* eslint-disable no-unused-vars */
import React, { useState, useEffect } from 'react';
import Monster from './Monster';
import Hero from './Hero';
import HealthBar from './HealthBar';
import TargetButton from './TargetButton';
import UpgradeScreen from './UpgradeScreen';
import StartScreen from './StartScreen';
import GameOverScreen from './GameOverScreen';
import Timer from './Timer';
import './App.css';

const Game = () => {
  const [gameState, setGameState] = useState('start');
  const [wave, setWave] = useState(1);
  const [monsterHP, setMonsterHP] = useState(100);
  const [monsterMaxHP, setMonsterMaxHP] = useState(100);
  const [monsterDamage, setMonsterDamage] = useState(10);
  const [heroHP, setHeroHP] = useState(20);
  const [heroMaxHP, setHeroMaxHP] = useState(20);
  const [heroDamage, setHeroDamage] = useState(8);
  const [exp, setExp] = useState(0);
  const [isTargetVisible, setIsTargetVisible] = useState(false);
  const [monsterCooldown, setMonsterCooldown] = useState(5000);
  const [heroCooldown, setHeroCooldown] = useState(3000);

  useEffect(() => {
    if (gameState === 'playing') {
      const monsterAttackInterval = setInterval(() => {
        setHeroHP((prevHP) => Math.max(0, prevHP - monsterDamage));
        setMonsterCooldown(5000);
      }, monsterCooldown);

      const heroAttackInterval = setInterval(() => {
        setMonsterHP((prevHP) => Math.max(0, prevHP - heroDamage));
        setHeroCooldown(3000);
      }, heroCooldown);

      const cooldownTickInterval = setInterval(() => {
        setMonsterCooldown((prev) => Math.max(0, prev - 100));
        setHeroCooldown((prev) => Math.max(0, prev - 100));
      }, 100);

      const targetAppearInterval = setInterval(() => {
        setIsTargetVisible(Math.random() < 0.3); // 30% chance to appear every second
      }, 1000);

      return () => {
        clearInterval(monsterAttackInterval);
        clearInterval(heroAttackInterval);
        clearInterval(cooldownTickInterval);
        clearInterval(targetAppearInterval);
      };
    }
  }, [gameState, monsterDamage, heroDamage, monsterCooldown, heroCooldown]);

  useEffect(() => {
    if (heroHP <= 0) {
      setExp((prevExp) => prevExp + wave * 10);
      setWave((prevWave) => prevWave + 1);
      setGameState('upgrade');
    } else if (monsterHP <= 0) {
      setGameState('gameOver');
    }
  }, [heroHP, monsterHP, wave]);

  const handleUpgrade = (type, amount) => {
    if (type === 'damage') {
      setMonsterDamage((prev) => prev + amount);
    } else if (type === 'health') {
      setMonsterMaxHP((prev) => prev + amount);
    }
    setExp((prev) => prev - amount * 10);
  };

  const startNewWave = () => {
    setHeroHP(heroMaxHP + wave * 20);
    setHeroMaxHP(heroMaxHP + wave * 20);
    setHeroDamage(heroDamage + wave * 2);
    setMonsterHP(monsterMaxHP);
    setGameState('playing');
  };

  return (
    <div className="game-container">
      {gameState === 'start' && <StartScreen onStart={() => setGameState('playing')} />}
      {gameState === 'playing' && (
        <>
          <div className="info-bar">
            <span>Wave: {wave}</span>
            <span>EXP: {exp}</span>
          </div>
          <div className="battle-area">
            <div className="monster-container">
              <Monster />
              <HealthBar current={monsterHP} max={monsterMaxHP} />
              <Timer cooldown={monsterCooldown} maxCooldown={5000} />
            </div>
            <div className="hero-container">
              <Hero />
              <HealthBar current={heroHP} max={heroMaxHP} />
              <Timer cooldown={heroCooldown} maxCooldown={3000} />
              <TargetButton 
                onClick={() => setMonsterCooldown(prev => Math.max(0, prev - 250))} 
                isVisible={isTargetVisible} 
              />
            </div>
          </div>
        </>
      )}
      {gameState === 'upgrade' && (
        <UpgradeScreen 
          exp={exp} 
          onUpgrade={handleUpgrade} 
          onContinue={startNewWave} 
        />
      )}
      {gameState === 'gameOver' && (
        <GameOverScreen 
          wave={wave} 
          exp={exp} 
          onRestart={() => {
            setGameState('start');
            setWave(1);
            setMonsterHP(100);
            setMonsterMaxHP(100);
            setMonsterDamage(10);
            setHeroHP(100);
            setHeroMaxHP(100);
            setHeroDamage(8);
            setExp(0);
          }} 
        />
      )}
    </div>
  );
};

export default Game;
