// src/GameOverScreen.js
import React from 'react';

const GameOverScreen = ({ wave, exp, onUpgrade, damageCost, healthCost, onRestart }) => {
  return (
    <div className="game-over-screen">
      <h2>Game Over</h2>
      <p>You survived to wave {wave}</p>
      <p>Total EXP: {exp}</p>
      <div className="upgrade-options">
        <button onClick={() => onUpgrade('damage')} disabled={exp < damageCost}>
          Upgrade Damage (Cost: {damageCost} EXP)
        </button>
        <button onClick={() => onUpgrade('health')} disabled={exp < healthCost}>
          Upgrade Health (Cost: {healthCost} EXP)
        </button>
      </div>
      <button onClick={onRestart}>Restart Game</button>
    </div>
  );
};

export default GameOverScreen;
